import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import {Button, Navbar,Nav,Form,NavDropdown} from 'react-bootstrap';
import Logo from './logo.PNG';
import DropdownButton from 'react-bootstrap/DropdownButton'
import DropdownToggle from 'react-bootstrap/esm/DropdownToggle';

class Header extends Component {



  renderContent() {
    switch (this.props.auth) {
      case null:
        return;
      case false:
        return <Button variant="btn btn-info mr-3" a href="/auth/google">Login with google</Button>;
      default:
        return [
          <Nav >
             <a className="nav-link dropdown-toggle" href="#" id="navbarDropdownMenuLink" role="button" data-toggle="dropdown"  aria-expanded="false">
               <img src={this.props.auth ? this.props.auth.bilde : "https://cdn.iconscout.com/icon/premium/png-256-thumb/profile-1506810-1278719.png"} style={{width:"40", height:"40" }}/>
            </a>
             <div className="dropdown-menu">
               <a className="dropdown-item" href="#">Dashboard</a>
               <a className="dropdown-item" href="#">Edit Profile</a>
               <a className="dropdown-item" href="#">Log Out</a>
             </div>
        

     </Nav>
        
        ];
    }
  }

  render() {
    return (
      <div className="Row">
        <div className="Col">
          <Navbar variant="dark">
          <Navbar.Brand href="/">
              MovieSelector
              <img
                alt="Logo bilde"
                src={Logo}
                width="50"
                height="50"
                className="ml-2"

            />
    </Navbar.Brand>
      <Nav className="mr-auto">
        <Nav.Link href="#home">Home</Nav.Link>
        <Nav.Link href="/about">About</Nav.Link>
        

      </Nav>
      <Form inline>
      <h4 className="text-white pr-2">{this.props.auth ? this.props.auth.name : ""}</h4>
      {this.renderContent()}
      </Form>
    </Navbar>
    </div>
  </div>
    );
  }
}

function mapStateToProps({ auth }) {
  return { auth };
}

export default connect(mapStateToProps)(Header);
