import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import {Button, Navbar,Nav,Form,NavDropdown} from 'react-bootstrap';
import Logo from './logo.PNG';
import DropdownButton from 'react-bootstrap/DropdownButton'
import DropdownToggle from 'react-bootstrap/esm/DropdownToggle';

class Header extends Component {



  renderContent() {
    switch (this.props.auth) {
      case null:
        return;
      case false:
        return <Button variant="btn btn-info mr-3" a href="/auth/google">Login with google</Button>;
      default:
        return [
          <NavDropdown title="Dropdown" id="basic-nav-dropdown">
            <Navbar.Brand href="#home">
      <img
        src="/logo.svg"
        width="30"
        height="30"
        className="d-inline-block align-top"
        alt="React Bootstrap logo"
      />
    </Navbar.Brand>
        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
        <NavDropdown.Divider />
        <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
      </NavDropdown>
      
        
        ];
    }
  }

  render() {
    return (
      <div className="Row">
        <div className="Col">
          <Navbar variant="dark">
          <Navbar.Brand href="/">
              MovieSelector
              <img
                alt="Logo bilde"
                src={Logo}
                width="50"
                height="50"
                className="ml-2"

            />
    </Navbar.Brand>
      <Nav className="mr-auto">
        <Nav.Link href="#home">Home</Nav.Link>
        <Nav.Link href="/about">About</Nav.Link>
        

      </Nav>
      <Form inline>
      <h4 className="text-white pr-2">{this.props.auth ? this.props.auth.name : ""}</h4>
      {this.renderContent()}
      </Form>
    </Navbar>
    </div>
  </div>
    );
  }
}

function mapStateToProps({ auth }) {
  return { auth };
}

export default connect(mapStateToProps)(Header);
